Method.Implementation:
s ext = ..extExtract( code )
s list = ##class(sc.options).handlers( ext ) ;handlers list for event
s printer = ##class(sc.printer).new() ;read settings or return default
if ( list = "" ) {
s msg = event _ "( " _ code _" ). Warning: handlers for '" _ ext _ "' not set!" d printer.warn( msg ) Q 1
}
s ll = $l(list,","), class = "", result = 1
for i=1:1:ll {
s class = $p( list, ",", i ) s handler = $zobjclassmethod( class, "%New" ) ;$zobjclassmethod -> $classmethod s handler.code = code, handler.event = event
if '$IsObject( handler ) { d printer.error( "Error! Can't create handler " _ handler ) continue }
s sc = handler.handle()
do ##class(%SYSTEM.Status).AppendStatus( result, sc )
}
Q sc

k subdir s subdir = "", sc = 1
s rs = ##class(%ResultSet).%New("%File:FileSet") s sc = rs.Execute( dir, mask, , 1 )
while rs.Next() {
s file = $g(rs.Data("Name")) s type = $g(rs.Data("Type")) s isDirectory = ( type = "D" )
if ( isDirectory ){ s subdir( $i( subdir ) ) = file continue }
s code = "", sc = $system.OBJ.Load( file, "k-d", , .code ) ;"k-d" - load only
w !, file _ " -> " _ code, " ", +sc
if ( 'sc ) continue
s loaded = loaded _ "," _ code ;save name of loaded document
} d rs.Close()
#; for each subdir s i = "" for { s i = $o( subdir( i ), 1, dir ) Q:i="" s sc = ..importDir( dir, mask, .loaded ) Q:'sc }
Q sc

s key = $g(key), val = $g(val)
s storage = ..storage()
if key = "" { 
	s @storage = val
} else {
	s @storage@(key) = val
}
Q ..get(key)

if $d( workdir ){
	Q ..set( "", workdir )			
}
Q ..get( "", $zu(12,"") )
