Method.Implementation:
s replace = 1, verbose = 0, killAll = 0, forceName = "", loaded = ""
s sc = ##class(%DeepSee.UserLibrary.Utils).%Import( filename, replace, verbose, killAll, forceName, .loaded )
if ( sc ) {
	s ..code = loaded( 1 ) _ ".DFI"
}
Q sc

s printer = ##class(sc.printer).new() s handler = ..%New( "", "exportAll", printer )
s sql = "Select documentName From %DeepSee_UserLibrary.FolderItem" s rs = ##class(%SQL.Statement).%ExecDirect(,sql)
s sc = $$$OK while rs.%Next() {
s handler.code = rs.documentName if ( handler.ignore() ) { continue }
s sc = handler.export() Q:'sc
d printer.w( handler.code _ " -> " _ handler.filename(), sc )
}
Q sc
